var link=document.querySelector(".search-button"),form=document.querySelector(".search-form"),arrive=form.querySelector("[name=arrive]"),departure=form.querySelector("[name=departure]"),adults=form.querySelector("[name=adults]"),kids=form.querySelector("[name=kids]");link.addEventListener("click",function(evt){evt.preventDefault(),form.classList.toggle("form-show"), form.classList.remove("form-error"),arrive.focus()}),form.addEventListener("submit",function(evt){if(!arrive.value||!departure.value||!adults.value||!kids.value){evt.preventDefault(),form.classList.remove("form-error"),form.offsetWidth=form.offsetWidth,form.classList.add("form-error")}});
