var window=document.querySelector(".container"),mappic=document.querySelector(".map-pic"),map=document.querySelector(".map-hidden"),iframe=document.querySelector(".map-hidden"),link=document.querySelector(".search-button"),form=document.querySelector(".search-form"),arrive=form.querySelector("[name=arrive]"),departure=form.querySelector("[name=departure]"),adults=form.querySelector("[name=adults]"),kids=form.querySelector("[name=kids]");window.addEventListener("load",function(evt){evt.preventDefault(),form.classList.add("form-hidden"),mappic.classList.add("map-pic-hidden"),map.classList.remove("map-hidden")}),link.addEventListener("click",function(evt){evt.preventDefault(),form.classList.toggle("form-show"), form.classList.remove("form-error"),arrive.focus()}),form.addEventListener("submit",function(evt){if(!arrive.value||!departure.value||!adults.value||!kids.value){evt.preventDefault(),form.classList.remove("form-error"),form.offsetWidth=form.offsetWidth,form.classList.add("form-error")}});
